import m from "mithril"
import BeerList from "./BeerList.js"
import Actions from "./Actions.js"
import CompareBeers from "./CompareBeers.js"
import { getBeers } from "./model.js"
import { sortBy, prop, props, map } from "ramda"

const onError = mdl => ({ response }) => {
  mdl.state.errors = map(props(["param", "msg"]), response.data)
  mdl.state.data = undefined
}

const onSuccess = mdl => data => {
  mdl.state.errors = undefined
  mdl.state.data = data
}

const parseIds = ids => Object.keys(ids).map(id => parseInt(id))

const byComparison = ids => beer => parseIds(ids).includes(beer.id)

const Cards = () => {
  return {
    view: ({ attrs: { mdl, sortedByProp } }) =>
      m(".cards", [
        mdl.comparison.selections
          ? m(CompareBeers, {
              mdl,
              beers: mdl.state.data.filter(
                byComparison(mdl.comparison.beerList)
              ),
            })
          : m(BeerList, {
              mdl,
              beers: sortedByProp(mdl.state.data),
            }),
      ]),
  }
}

const Charts = () => {
  const toPlot = (dom, mdl) =>
    Plotly.newPlot(dom, mdl.state.data, {
      title: mdl.state.symbol,
    })

  return {
    oncreate: ({ dom, attrs: { mdl } }) => toPlot(dom, mdl),
    view: () => m(".chart", { id: "chart" }),
  }
}

const Beers = ({ attrs: { mdl } }) => {
  const load = () => {
    mdl.log("loading")(mdl)
    getBeers(mdl)(mdl.pagination).then(onSuccess(mdl), onError(mdl))
  }

  return {
    oninit: load,
    view: ({ attrs: { mdl } }) => {
      if (mdl.state.data) {
        const sortedByProp = sortBy(prop(mdl.comparison.selectionsBy))
        return [
          m(Actions, { mdl, load }),
          mdl.state.view == "cards"
            ? m(Cards, { mdl, sortedByProp })
            : m(Charts, { mdl, sortedByProp }),
        ]
      }
    },
  }
}

export default Beers
